INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (11,'acetylcholine','Vaak voorkomende neurotransmitter, bindt aan receptoren en verandert de permeabiliteit van het postsynaptische membraan voor specifieke ionen.<br>\r\n<a href=\"http://www.bioplek.org/animaties/zenuwstelsel/synaps.html\" target=\"_blank\"><B>animatie synaps</B></a> van <B><FONT COLOR=\"#008000\">bio</FONT><FONT COLOR=\"#FF0000\">plek</FONT></B>.');
