INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (13,'acquired immuno deficiency syndrome','AIDS, naam voor de latere stadia van een HIV-infectie, waarbij het aantal T-cellen verminderd is en er veel secundaire infecties voorkomen.\r\n');
