INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (37,'afweersysteem','Het geheel van reacties op het binnendringen van ziekteverwekkers en andere lichaamsvreemde stoffen. Berust op mechanische afweer, aspecifieke afweer en specifieke afweer.\r\n<br>\r\n<a href=\"http://www.biodoen.nl/B4030OL01.php\" target=\"_blank\"><B>Oefeningen specifieke, a-specifieke afweer en immuniteit</B></a> \r\nvan <B><FONT COLOR=\"#00FF00\">bio</FONT>doen</B>.<br>\r\n<a href=\"http://www.biodoen.nl/B4035EX01.php\" target=\"_blank\"><B>Oefenvragen examenopdrachten afweer</B></a> van <B><FONT COLOR=\"#00FF00\">bio</FONT>doen</B>.<br>\r\n<a href=\"http://www.bioplek.org/animaties/afweer/overzicht.html\" target=\"_blank\"><B>Overzicht afweer</B></a> van <B><FONT COLOR=\"#008000\">bio</FONT><FONT COLOR=\"#FF0000\">plek</FONT></B>.');
INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (38,'agonistisch gedrag','Gedrag dat samenhangt met conflicten tussen individuen (agressie). Voorbeeld: aanvallen, vluchten, dreigen.<br>\r\n1. Motivatie tot dreigen en aanvallen<br>\r\n2. Dreigen of aanvallen<br>\r\nHet gedrag van roofdieren ten opzichte van prooidieren wordt niet als agressie beschouwd.\r\n');
