INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (608,'lederhuid','Laag van de huid, gelegen tussen de opperhuid en het onderhuidse bindweefsel. In de lederhuid liggen de warmte-, koude-, druk- en tastzintuigen. Verder liggen er in de lederhuid zenuwen met pijnpunten, haarspiertjes, bloedvaten en zweetklieren.\r\n<br>\r\n<a href=\"http://www.biodoen.nl/B2602OT01.php\" target=\"_blank\"><B>Oefenvragen de huid</B></a> van <B><FONT COLOR=\"#00FF00\">bio</FONT>doen</B>.');
INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (609,'leerproces','Proces waarbij het gedag van het dier wordt bijgesteld door de inwerking van omgevingsprikkels.');
INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (610,'lengtegroei','Groei in de lengte. Lengtegroei vindt bij planten alleen in de groei- en strekkingszone plaats door celgroei en celstrekking. Lengtegroei bij gewervelden vindt voornamelijk plaats in de groeischijven.');
