INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (637,'Luteiniserend hormoon','Luteiniserend hormoon (LH) is een hormoon, dat afgescheiden wordt door de voorkwab van de hypofyse en invloed heeft op de interstitiele cellen in de testes en de follikel in een eierstok.');
