INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (639,'lymfevatenstelsel','Stelsel van kanalen waardoorheen de lymfe vanuit de weefsels naar het bloedvatstelsel loopt. In de lymfevaten bevinden zich kleppen voor eenrichtingverkeer. Vele lymfevaten sluiten aan op een lymfeknoop. Na de lymfeknoop wordt de lymfe door een groter lymfevat afgevoerd.');
INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (640,'lymfeknoop','Lymfeknoop (lymfeklier) is een orgaantje in het lymfestelsel. In lymfeknopen worden antistoffen geproduceerd, ziekteverwekkers gefagocyteerd en celresten opgeruimd. Ook worden er lymfocyten gevormd. Elke lymfeknoop heeft een afvoerend lymfevat.');
INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (641,'lymfocyt','Bepaald type witte bloedcellen. Lymfocyten hebben een grote kern. Er zijn diverse soorten lymfocyten.');
