INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (643,'M-fase','M-fase of mitose is de fase waarin de kerndeling plaatsvindt. De M-fase bestaat uit profase, metafase, anafase en telofase.');
INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (644,'maagportier','Maagportier of pylorus is de kringspier tussen de maag en de twaalfvingerige darm. De maagportier werkt o.i.v. de pH in de twaalfvingerige darm. De maagportier laat een klein beetje van de voedselbrij door naar de twaalfvingerige darm.');
