INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (661,'mergschede','Mergschede of myeleÃ¯neschede is de omhulling van een (lange) zenuwceluitloper. Een  mergschede bestaat uit platte cellen, die om een uitloper gerold zijn. Tussen twee opeenvolgende cellen ligt een kleine ruimte, de insnoering.');
