INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (684,'middenoor','Gedeelte van een oor dat ligt tussen het uitwendige oor en het binnenoor (slakkenhuis). Het middenoor loopt van trommelvlies naar het slakkenhuis en is door middel van de buis van Eustachius verbonden met de keelholte.');
