INSERT INTO `concepts` (`id`,`name`,`description`) VALUES (687,'milieu, extern','Ruimte buiten het organisme en binnen het organisme in holten die aangesloten zijn op de buitenwereld. De darmholte en de luchtwegen zijn voorbeelden van het externe milieu.');
